import Vue from 'vue'
import Vuex from "vuex";
//只有操作没有数据 数据通过服务器获得
const actions = {}
//修改操作state
const mutations = {
    id(state, value) {
        state.id = value
    },
    name(state, value) {
        state.name = value
    },
    place(state, value) {
        state.place = value
    },
    age(state, value) {
        state.age = value
    },
    intro(state, value) {
        state.intro = value
    },
    sex(state, value) {
        state.sex = value
    },
    bookid(state, value) {
        state.bookid = value
    },
    createid(state, value) {
        state.createid = value
    },//建立新书

}
//数据信息
const state = {
    id: '',
    age: '',
    name: '',
    place: '',
    intro: '',
    sex: '',
    bookid: '',
    createid: "",
}
Vue.use(Vuex);
export default new Vuex.Store({
    actions,
    mutations,
    state
})
