import Vue from 'vue'
import Vuex from "vuex";
//只有操作没有数据 数据通过服务器获得
const actions = {}
//修改操作state
const mutations = {
    userid(state, value) {
        state.userid = value
    },
    authorid(state, value) {
        state.authorid = value
    },
    name(state, value) {
        state.name = value
    },
    place(state, value) {
        state.place = value
    },
    age(state, value) {
        state.age = value
    },
    intro(state, value) {
        state.intro = value
    },

}
//数据信息
const state = {
    userid: '',
    authorid: '',
    age: '',
    name: '',
    place: '',
    intro: '',
}
Vue.use(Vuex);
export default new Vuex.Store({
    actions,
    mutations,
    state
})
