import Vuex from "vuex";
//只有操作没有数据 数据通过服务器获得
const actions = {}
//修改操作state
const mutations = {}
//数据信息
const state = {}
Vue.use(Vuex);
export default new Vuex.Store({
    actions,
    mutations,
    state
})
